// Get input from Agent or direct command
const input = $input.first().json;

// If it's from Agent with structured data, extract from markdown
if (input.output) {
  // Agent returned markdown-wrapped JSON
  const agentOutput = input.output;
  console.log('Agent output:', agentOutput);

  // Extract JSON from markdown code blocks
  const jsonMatch = agentOutput.match(/```json\s*(\{.*?\})\s*```/s);
  if (jsonMatch) {
    try {
      const parsedData = JSON.parse(jsonMatch[1]);
      console.log('Parsed Agent data:', parsedData);

      if (parsedData.action && parsedData.tool !== undefined && parsedData.parameters) {
        return {
          action: parsedData.action,
          tool: parsedData.tool,
          parameters: parsedData.parameters
        };
      }
    } catch (e) {
      console.log('Failed to parse Agent JSON:', e);
    }
  }
}

// Fallback for direct commands
let command = '';
if (input.chatInput) {
  command = input.chatInput;
} else if (input.command) {
  command = input.command;
} else {
  command = JSON.stringify(input);
}

console.log('Fallback processing command:', command);

const lowerCommand = command.toLowerCase().trim();

let output = {
  originalCommand: command,
  action: null,
  tool: null,
  parameters: {}
};

// Email update pattern
const emailPattern = /change\s+email\s+of\s+task\s+(\w+)\s+to\s+([\w._%+-]+@[\w.-]+\.[A-Za-z]{2,})/i;
const emailMatch = command.match(emailPattern);

if (emailMatch) {
  output.action = 'update_email';
  output.tool = 'monday_email_updater';
  output.parameters = {
    itemName: emailMatch[1],
    email: emailMatch[2]
  };
  return output;
}

// Find task pattern
const findPattern = /find\s+task\s+(\w+)/i;
const findMatch = command.match(findPattern);

if (findMatch) {
  output.action = 'search_item';
  output.tool = 'get_board_items_by_name';
  output.parameters = {
    boardId: 2056518483,
    term: findMatch[1]
  };
  return output;
}

// List all tasks
if (lowerCommand.includes('list all') || lowerCommand.includes('show all')) {
  output.action = 'list_all';
  output.tool = 'get_board_items_by_name';
  output.parameters = {
    boardId: 2056518483,
    term: ""
  };
  return output;
}

// Board info
if (lowerCommand.includes('board info') || lowerCommand.includes('board details')) {
  output.action = 'board_info';
  output.tool = 'get_board_schema';
  output.parameters = {
    boardId: 2056518483
  };
  return output;
}

// Help
if (lowerCommand === 'help' || lowerCommand === 'hi' || lowerCommand === 'hello') {
  output.action = 'help';
  output.tool = null;
  output.parameters = {};
  return output;
}

// Unknown
output.action = 'unknown';
output.tool = null;
output.parameters = {};

return output;

// Get the MCP response
const response = $input.item.json;
const originalData = $input.item;

// Check if we have a valid result
if (response.result && response.result.content) {
  const content = response.result.content[0];

  // Parse the content based on action type
  const action = originalData.action || 'unknown';

  if (action === 'update_email') {
    // Email update response
    if (content.text.includes('successfully')) {
      return {
        success: true,
        message: `‚úÖ Email updated for task "${originalData.parameters.itemName}" to ${originalData.parameters.email}`,
        details: content.text
      };
    } else {
      return {
        success: false,
        message: `‚ùå Failed to update email: ${content.text}`
      };
    }
  } else if (action === 'search_item' || action === 'list_all') {
    // Search or list response
    try {
      const data = JSON.parse(content.text);
      if (data.items && data.items.length > 0) {
        const items = data.items.map(item => ({
          id: item.id,
          name: item.name,
          email: item.column_values?.find(col => col.id === 'email_mktp3awp')?.text || 'No email set'
        }));

        return {
          success: true,
          message: `üìã Found ${items.length} task(s)`,
          items: items
        };
      } else {
        return {
          success: true,
          message: 'üì≠ No tasks found',
          items: []
        };
      }
    } catch (e) {
      return {
        success: false,
        message: '‚ùå Error parsing response',
        error: e.message,
        rawResponse: content.text
      };
    }
  } else if (action === 'board_info') {
    // Board schema response
    try {
      const data = JSON.parse(content.text);
      if (data.board) {
        return {
          success: true,
          message: `üìä Board: ${data.board.name}`,
          boardId: data.board.id,
          columns: data.board.columns?.map(col => ({
            id: col.id,
            title: col.title,
            type: col.type
          })) || []
        };
      } else {
        return {
          success: false,
          message: '‚ùå Board not found'
        };
      }
    } catch (e) {
      return {
        success: false,
        message: '‚ùå Error parsing board info',
        error: e.message,
        rawResponse: content.text
      };
    }
  } else {
    // Generic response
    return {
      success: true,
      message: content.text
    };
  }
} else if (response.error) {
  // Handle MCP error
  return {
    success: false,
    message: `‚ùå MCP Error: ${response.error.message || 'Unknown error'}`,
    error: response.error
  };
} else {
  // Unknown response format
  return {
    success: false,
    message: '‚ùå Unexpected response format',
    rawResponse: response
  };
}

// Return help information
return {
  success: true,
  message: "ü§ñ Monday.com Chatbot Commands",
  commands: [
    {
      command: "change email of task [name] to [email]",
      description: "Update email for a specific task",
      example: "change email of task 1 to user@example.com"
    },
    {
      command: "find task [name]",
      description: "Search for a specific task",
      example: "find task 2"
    },
    {
      command: "list all tasks",
      description: "Show all tasks in the board",
      example: "list all tasks"
    },
    {
      command: "board info",
      description: "Get board details and structure",
      example: "board info"
    },
    {
      command: "help",
      description: "Show this help message",
      example: "help"
    }
  ],
  tip: "üí° Commands are not case-sensitive. Task names are: 1, 2, or 3"
};

// Return error for unknown command
const command = $input.item.json.originalCommand || 'No command provided';

return {
  success: false,
  message: "‚ùå Unknown command",
  error: `I don't understand: "${command}"",
  suggestion: "Try 'help' to see available commands",
  availableCommands: [
    "change email of task [name] to [email]",
    "find task [name]",
    "list all tasks",
    "board info",
    "help"
  ]
};

