// Natural Language Processing for Monday.com commands
const body = $input.first().json;
const userMessage = body.message || body.text || body.command || '';
const lowerMessage = userMessage.toLowerCase();

console.log('ü§ñ Received message:', userMessage);

// Parse different command patterns
let command = {
  action: 'unknown',
  tool: null,
  params: {},
  originalMessage: userMessage
};

// Email update patterns
const emailMatch = userMessage.match(/change\s+(?:the\s+)?email\s+(?:of\s+)?(?:task\s+)?(\w+)\s+to\s+([^\s]+@[^\s]+)/i);
if (emailMatch) {
  command = {
    action: 'update_email',
    tool: 'monday_email_updater',
    params: {
      itemName: emailMatch[1],
      email: emailMatch[2]
    },
    originalMessage: userMessage
  };
}

// Search/find patterns
const searchMatch = userMessage.match(/find\s+(?:task\s+)?(\w+)/i);
if (searchMatch) {
  command = {
    action: 'search_task',
    tool: 'get_board_items_by_name',
    params: {
      boardId: 2056518483,
      term: searchMatch[1]
    },
    originalMessage: userMessage
  };
}

// List all patterns
if (lowerMessage.includes('list all') || lowerMessage.includes('show all') || lowerMessage.includes('what tasks')) {
  command = {
    action: 'list_all',
    tool: 'get_board_items_by_name',
    params: {
      boardId: 2056518483,
      term: ''
    },
    originalMessage: userMessage
  };
}

// Board info patterns
if (lowerMessage.includes('board info') || lowerMessage.includes('board details') || lowerMessage.includes('columns')) {
  command = {
    action: 'board_info',
    tool: 'get_board_schema',
    params: {
      boardId: 2056518483
    },
    originalMessage: userMessage
  };
}

// Help patterns
if (lowerMessage.includes('help') || lowerMessage.includes('what can you do')) {
  command = {
    action: 'help',
    tool: null,
    params: {},
    originalMessage: userMessage
  };
}

console.log('üîç Parsed command:', command);
return command;

// Format MCP response for chatbot
const mcpResponse = $input.first().json;
const originalMessage = $('Natural Language Parser').first().json.originalMessage;
const action = $('Natural Language Parser').first().json.action;

console.log('üì® MCP Response:', mcpResponse);

let responseText = '';
let success = false;
let data = null;

if (mcpResponse.result) {
  success = true;

  // Handle different response formats
  if (mcpResponse.result.content && mcpResponse.result.content[0]) {
    responseText = mcpResponse.result.content[0].text;
  } else if (mcpResponse.result.message) {
    responseText = mcpResponse.result.message;
  } else if (mcpResponse.result.status === 'success') {
    responseText = mcpResponse.result.message || 'Command executed successfully!';
  } else {
    responseText = JSON.stringify(mcpResponse.result, null, 2);
  }

  data = mcpResponse.result;
} else if (mcpResponse.error) {
  success = false;
  responseText = `‚ùå Error: ${mcpResponse.error.message}`;
} else {
  success = false;
  responseText = '‚ùå Unexpected response from MCP server';
}

// Add emoji based on action
let emoji = 'ü§ñ';
if (action === 'update_email') emoji = 'üìß';
else if (action === 'search_task') emoji = 'üîç';
else if (action === 'list_all') emoji = 'üìã';
else if (action === 'board_info') emoji = 'üìä';

return {
  success,
  message: `${emoji} ${responseText}`,
  originalCommand: originalMessage,
  action: action,
  timestamp: new Date().toISOString(),
  data: data,
  mcpResponse: mcpResponse
};

// Handle unknown commands
const originalMessage = $('Natural Language Parser').first().json.originalMessage;

const unknownText = `‚ùì I don't understand that command.

Try these examples:
‚Ä¢ "change email of task 1 to user@example.com"
‚Ä¢ "find task 2"
‚Ä¢ "list all tasks"
‚Ä¢ "board info"
‚Ä¢ "help"

Type "help" to see all available commands!`;

return {
  success: false,
  message: unknownText,
  originalCommand: originalMessage,
  action: 'unknown',
  timestamp: new Date().toISOString()
};

// Log all chatbot interactions
const data = $input.first().json;
const timestamp = new Date().toISOString();

console.log(`\nü§ñ [${timestamp}] Monday.com Chatbot Interaction:`);
console.log(`   User: ${data.originalCommand}`);
console.log(`   Action: ${data.action}`);
console.log(`   Response: ${data.message.substring(0, 100)}...`);
console.log(`   Success: ${data.success}\n`);

// You can extend this to send to external services:
// - Discord webhook
// - Slack channel
// - Database logging
// - Analytics service

return data;
