const body = $input.first().json;
const userMessage = body.message || body.text || body.command || '';
const lowerMessage = userMessage.toLowerCase();

console.log('Input message:', userMessage);

// Email update patterns
const emailMatch = userMessage.match(/change\s+(?:the\s+)?email\s+(?:of\s+)?(?:task\s+)?(\w+)\s+to\s+([^\s]+@[^\s]+)/i);
if (emailMatch) {
  console.log('Email match found');
  return {
    tool: 'monday_email_updater',
    params: {
      itemName: emailMatch[1],
      email: emailMatch[2]
    }
  };
}

// Search patterns
const searchMatch = userMessage.match(/find\s+(?:task\s+)?(\w+)/i);
if (searchMatch) {
  console.log('Search match found');
  return {
    tool: 'get_board_items_by_name',
    params: {
      boardId: 2056518483,
      term: searchMatch[1]
    }
  };
}

// List all patterns
if (lowerMessage.includes('list all') || lowerMessage.includes('show all')) {
  console.log('List all match found');
  return {
    tool: 'get_board_items_by_name',
    params: {
      boardId: 2056518483,
      term: ''
    }
  };
}

// Board info patterns
if (lowerMessage.includes('board info') || lowerMessage.includes('board details')) {
  console.log('Board info match found');
  return {
    tool: 'get_board_schema',
    params: {
      boardId: 2056518483
    }
  };
}

// Help patterns
if (lowerMessage.includes('help')) {
  console.log('Help match found');
  return {
    tool: 'get_board_schema',
    params: {
      boardId: 2056518483
    }
  };
}

// Default response for unknown commands
console.log('No match found, using default');
return {
  tool: 'get_board_schema',
  params: {
    boardId: 2056518483
  }
};
